package JavaBasic;

public class EvenOROddNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          int x=111;
          //int y=7;
          if(x%2==0) {
        	  System.out.println("this number is even"+ x);
          }else {
        	  System.out.println("this number is odd"+ x);
          }
	}

}
