package JavaBasic;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=2;
		while(i<=10) { //2,4,6,8,10
			System.out.println(i);
			i+=2;
		}
		
		System.out.println("------------------------Reverse number");
		int j=10;
		while(j>0) {
			System.out.println(j);
			j--;
		}
		
		

	}

}
