package TechnicalInterview;
//
//Assigning a Variable to the int Variable
//Using Type-Casting


public class AsciiValue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		////Assigning a Variable to the int Variable
		char a='A';
		char b='b';
		char c='c';
		int asciA=a;
		int asciB=b;
		int asciC=c;
		
		System.out.println("A= "+asciA);
		System.out.println("b= "+asciB);
		System.out.println("c="+asciC);
		
		////Using Type-Casting
		char D='D';
		int asciD=(int) D;
		System.out.println("D= "+asciD);

	}

}
